d = {}
soup = BeautifulSoup(html)
for li in soup.findAll("li"):
    if len(li.text) > 0:
        original = li.text
        airport_code = original[original.find("(") + 1:original.find(")")]
        d[airport_code] = original

a = open("../data/airlines/azul/airports.data", "w")
a.write(json.dumps(d).encode('utf8'))
a.close()
